      "task_order": 2,
      "dependencies": [1],
      "inputs": {
        "implementation_task_order": 1,
        "original_prompt": "build a tic tac toe app"
      },
      "validation_criteria": {
        "critical": [
          "Returns pass/fail verdict based on evaluation",
          "All critical criteria from task 1 are tested and evaluated",
          "All 8 win conditions are verified (3 rows + 3 columns + 2 diagonals)"
        ],
        "expected": [
          "Provides specific feedback with examples if failing",
          "Tests edge cases and game logic thoroughly"
        ],
        "nice_to_have": []
      },
      "timeout_seconds": 1800
    }
  ]
}
