/* CSS Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    /* Color Palette */
    --bg-primary: #1a1a1a;
    --bg-secondary: #2d2d2d;
    --bg-display: #1e1e1e;
    --text-primary: #ffffff;
    --text-secondary: #b0b0b0;

    /* Button Colors */
    --btn-number: #3a3a3a;
    --btn-number-hover: #4a4a4a;
    --btn-operator: #ff9500;
    --btn-operator-hover: #ffb143;
    --btn-operator-active: #cc7700;
    --btn-function: #505050;
    --btn-function-hover: #606060;
    --btn-equals: #ff9500;
    --btn-equals-hover: #ffb143;

    /* Spacing */
    --spacing-xs: 0.5rem;
    --spacing-sm: 1rem;
    --spacing-md: 1.5rem;
    --spacing-lg: 2rem;

    /* Border Radius */
    --radius-sm: 8px;
    --radius-md: 12px;
    --radius-lg: 20px;

    /* Shadows */
    --shadow-sm: 0 2px 4px rgba(0, 0, 0, 0.2);
    --shadow-md: 0 4px 8px rgba(0, 0, 0, 0.3);
    --shadow-lg: 0 8px 16px rgba(0, 0, 0, 0.4);
}

/* Body Styles */
body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
        'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
        sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: var(--spacing-sm);
}

/* Calculator Container */
.calculator-container {
    width: 100%;
    max-width: 400px;
    margin: 0 auto;
}

.calculator {
    background: var(--bg-secondary);
    border-radius: var(--radius-lg);
    padding: var(--spacing-md);
    box-shadow: var(--shadow-lg);
    backdrop-filter: blur(10px);
}

/* Display Styles */
.display-container {
    background: var(--bg-display);
    border-radius: var(--radius-md);
    padding: var(--spacing-md);
    margin-bottom: var(--spacing-md);
    min-height: 120px;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    align-items: flex-end;
    word-wrap: break-word;
    word-break: break-all;
    overflow: hidden;
}

.display-previous {
    color: var(--text-secondary);
    font-size: 1.2rem;
    min-height: 1.5rem;
    margin-bottom: var(--spacing-xs);
    opacity: 0.8;
}

.display-current {
    color: var(--text-primary);
    font-size: 2.5rem;
    font-weight: 300;
    min-height: 3rem;
    line-height: 1.2;
}

/* Buttons Grid */
.buttons-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: var(--spacing-sm);
}

/* Button Base Styles */
.btn {
    background: var(--btn-number);
    color: var(--text-primary);
    border: none;
    border-radius: var(--radius-sm);
    font-size: 1.5rem;
    font-weight: 400;
    padding: var(--spacing-md);
    cursor: pointer;
    transition: all 0.15s ease;
    box-shadow: var(--shadow-sm);
    min-height: 64px;
    display: flex;
    align-items: center;
    justify-content: center;
    user-select: none;
    -webkit-tap-highlight-color: transparent;
}

.btn:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-md);
}

.btn:active {
    transform: translateY(0);
    box-shadow: var(--shadow-sm);
}

.btn:focus-visible {
    outline: 3px solid rgba(255, 255, 255, 0.5);
    outline-offset: 2px;
}

/* Number Buttons */
.btn-number {
    background: var(--btn-number);
}

.btn-number:hover {
    background: var(--btn-number-hover);
}

/* Zero Button (Spans 2 Columns) */
.btn-zero {
    grid-column: span 2;
}

/* Operator Buttons */
.btn-operator {
    background: var(--btn-operator);
    font-weight: 500;
}

.btn-operator:hover {
    background: var(--btn-operator-hover);
}

.btn-operator.active {
    background: var(--btn-operator-active);
    box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.3);
}

/* Function Buttons (AC, DEL, %) */
.btn-function {
    background: var(--btn-function);
    font-size: 1.2rem;
}

.btn-function:hover {
    background: var(--btn-function-hover);
}

/* Equals Button */
.btn-equals {
    background: var(--btn-equals);
    font-weight: 600;
    font-size: 2rem;
}

.btn-equals:hover {
    background: var(--btn-equals-hover);
}

/* Error State */
.display-current.error {
    color: #ff6b6b;
    font-size: 1.5rem;
}

/* Responsive Design */
@media (max-width: 480px) {
    .calculator {
        padding: var(--spacing-sm);
    }

    .display-container {
        padding: var(--spacing-sm);
        min-height: 100px;
    }

    .display-current {
        font-size: 2rem;
    }

    .display-previous {
        font-size: 1rem;
    }

    .btn {
        font-size: 1.25rem;
        padding: var(--spacing-sm);
        min-height: 56px;
    }

    .btn-equals {
        font-size: 1.75rem;
    }

    .buttons-grid {
        gap: var(--spacing-xs);
    }
}

@media (max-width: 360px) {
    .display-current {
        font-size: 1.75rem;
    }

    .btn {
        font-size: 1.1rem;
        min-height: 48px;
    }

    .btn-equals {
        font-size: 1.5rem;
    }
}

/* Animations */
@keyframes shake {
    0%, 100% { transform: translateX(0); }
    10%, 30%, 50%, 70%, 90% { transform: translateX(-5px); }
    20%, 40%, 60%, 80% { transform: translateX(5px); }
}

.shake {
    animation: shake 0.5s;
}

/* Print Styles */
@media print {
    body {
        background: white;
    }

    .calculator {
        box-shadow: none;
        border: 1px solid #000;
    }
}
