// Automated tests for Tic-Tac-Toe game logic
const { JSDOM } = require('jsdom');
const fs = require('fs');

// Read the files
const html = fs.readFileSync('index.html', 'utf8');
const js = fs.readFileSync('script.js', 'utf8');
const css = fs.readFileSync('styles.css', 'utf8');

// Create a DOM environment
const dom = new JSDOM(html, {
    runScripts: "outside-only",
    resources: "usable"
});

global.window = dom.window;
global.document = dom.window.document;
global.localStorage = {
    storage: {},
    getItem(key) { return this.storage[key] || null; },
    setItem(key, value) { this.storage[key] = value; }
};

// Execute the game script
eval(js);

console.log('ðŸ§ª Starting Tic-Tac-Toe Game Tests\n');

let testResults = {
    passed: 0,
    failed: 0,
    tests: []
};

function test(name, fn) {
    try {
        fn();
        console.log(`âœ… PASS: ${name}`);
        testResults.passed++;
        testResults.tests.push({ name, passed: true });
    } catch (error) {
        console.log(`âŒ FAIL: ${name}`);
        console.log(`   Error: ${error.message}`);
        testResults.failed++;
        testResults.tests.push({ name, passed: false, error: error.message });
    }
}

function assert(condition, message) {
    if (!condition) throw new Error(message);
}

// Test 1: Initial game state
test('Initial game state is correct', () => {
    assert(GameState.currentPlayer === 'X', 'Game should start with player X');
    assert(GameState.gameActive === true, 'Game should be active');
    assert(GameState.board.every(cell => cell === ''), 'Board should be empty');
});

// Test 2: Cell click updates board
test('Cell click updates board state', () => {
    resetGame();
    const cell = document.querySelector('[data-cell-index="0"]');
    cell.click();
    assert(GameState.board[0] === 'X', 'First move should be X');
    assert(cell.textContent === 'X', 'Cell should display X');
});

// Test 3: Player alternation
test('Players alternate turns', () => {
    resetGame();
    document.querySelector('[data-cell-index="0"]').click();
    assert(GameState.currentPlayer === 'O', 'Should switch to player O');
    document.querySelector('[data-cell-index="1"]').click();
    assert(GameState.currentPlayer === 'X', 'Should switch back to player X');
});

// Test 4: Cannot click same cell twice
test('Cannot click same cell twice', () => {
    resetGame();
    const cell = document.querySelector('[data-cell-index="0"]');
    cell.click();
    const firstValue = GameState.board[0];
    cell.click();
    assert(GameState.board[0] === firstValue, 'Cell value should not change on second click');
});

// Test 5: Win detection - Top row
test('Detects win in top row', () => {
    resetGame();
    // X wins top row: X X X
    document.querySelector('[data-cell-index="0"]').click(); // X
    document.querySelector('[data-cell-index="3"]').click(); // O
    document.querySelector('[data-cell-index="1"]').click(); // X
    document.querySelector('[data-cell-index="4"]').click(); // O
    document.querySelector('[data-cell-index="2"]').click(); // X wins
    assert(GameState.gameActive === false, 'Game should end after win');
    assert(GameState.scores.X === 1, 'X score should increment');
});

// Test 6: Win detection - Middle row
test('Detects win in middle row', () => {
    resetGame();
    GameState.scores = { X: 0, O: 0, ties: 0 };
    // O wins middle row: O O O
    document.querySelector('[data-cell-index="0"]').click(); // X
    document.querySelector('[data-cell-index="3"]').click(); // O
    document.querySelector('[data-cell-index="1"]').click(); // X
    document.querySelector('[data-cell-index="4"]').click(); // O
    document.querySelector('[data-cell-index="8"]').click(); // X
    document.querySelector('[data-cell-index="5"]').click(); // O wins
    assert(GameState.gameActive === false, 'Game should end after win');
    assert(GameState.scores.O === 1, 'O score should increment');
});

// Test 7: Win detection - Bottom row
test('Detects win in bottom row', () => {
    resetGame();
    GameState.scores = { X: 0, O: 0, ties: 0 };
    // X wins bottom row
    document.querySelector('[data-cell-index="6"]').click(); // X
    document.querySelector('[data-cell-index="0"]').click(); // O
    document.querySelector('[data-cell-index="7"]').click(); // X
    document.querySelector('[data-cell-index="1"]').click(); // O
    document.querySelector('[data-cell-index="8"]').click(); // X wins
    assert(GameState.gameActive === false, 'Game should end');
});

// Test 8: Win detection - Left column
test('Detects win in left column', () => {
    resetGame();
    GameState.scores = { X: 0, O: 0, ties: 0 };
    // X wins left column: 0, 3, 6
    document.querySelector('[data-cell-index="0"]').click(); // X
    document.querySelector('[data-cell-index="1"]').click(); // O
    document.querySelector('[data-cell-index="3"]').click(); // X
    document.querySelector('[data-cell-index="2"]').click(); // O
    document.querySelector('[data-cell-index="6"]').click(); // X wins
    assert(GameState.gameActive === false, 'Game should end');
});

// Test 9: Win detection - Middle column
test('Detects win in middle column', () => {
    resetGame();
    GameState.scores = { X: 0, O: 0, ties: 0 };
    // X wins middle column: 1, 4, 7
    document.querySelector('[data-cell-index="1"]').click(); // X
    document.querySelector('[data-cell-index="0"]').click(); // O
    document.querySelector('[data-cell-index="4"]').click(); // X
    document.querySelector('[data-cell-index="2"]').click(); // O
    document.querySelector('[data-cell-index="7"]').click(); // X wins
    assert(GameState.gameActive === false, 'Game should end');
});

// Test 10: Win detection - Right column
test('Detects win in right column', () => {
    resetGame();
    GameState.scores = { X: 0, O: 0, ties: 0 };
    // X wins right column: 2, 5, 8
    document.querySelector('[data-cell-index="2"]').click(); // X
    document.querySelector('[data-cell-index="0"]').click(); // O
    document.querySelector('[data-cell-index="5"]').click(); // X
    document.querySelector('[data-cell-index="1"]').click(); // O
    document.querySelector('[data-cell-index="8"]').click(); // X wins
    assert(GameState.gameActive === false, 'Game should end');
});

// Test 11: Win detection - Diagonal (top-left to bottom-right)
test('Detects win in diagonal (top-left to bottom-right)', () => {
    resetGame();
    GameState.scores = { X: 0, O: 0, ties: 0 };
    // X wins diagonal: 0, 4, 8
    document.querySelector('[data-cell-index="0"]').click(); // X
    document.querySelector('[data-cell-index="1"]').click(); // O
    document.querySelector('[data-cell-index="4"]').click(); // X
    document.querySelector('[data-cell-index="2"]').click(); // O
    document.querySelector('[data-cell-index="8"]').click(); // X wins
    assert(GameState.gameActive === false, 'Game should end');
});

// Test 12: Win detection - Diagonal (top-right to bottom-left)
test('Detects win in diagonal (top-right to bottom-left)', () => {
    resetGame();
    GameState.scores = { X: 0, O: 0, ties: 0 };
    // X wins diagonal: 2, 4, 6
    document.querySelector('[data-cell-index="2"]').click(); // X
    document.querySelector('[data-cell-index="0"]').click(); // O
    document.querySelector('[data-cell-index="4"]').click(); // X
    document.querySelector('[data-cell-index="1"]').click(); // O
    document.querySelector('[data-cell-index="6"]').click(); // X wins
    assert(GameState.gameActive === false, 'Game should end');
});

// Test 13: Tie detection
test('Detects tie when board is full with no winner', () => {
    resetGame();
    GameState.scores = { X: 0, O: 0, ties: 0 };
    // Create a tie game
    // X O X
    // X O O
    // O X X
    const moves = [0, 1, 2, 4, 3, 5, 7, 6, 8];
    moves.forEach(index => {
        document.querySelector(`[data-cell-index="${index}"]`).click();
    });
    assert(GameState.gameActive === false, 'Game should end');
    assert(GameState.scores.ties === 1, 'Tie score should increment');
});

// Test 14: Cannot move after game ends
test('Cannot make moves after game ends', () => {
    resetGame();
    // Create a winning game
    document.querySelector('[data-cell-index="0"]').click(); // X
    document.querySelector('[data-cell-index="3"]').click(); // O
    document.querySelector('[data-cell-index="1"]').click(); // X
    document.querySelector('[data-cell-index="4"]').click(); // O
    document.querySelector('[data-cell-index="2"]').click(); // X wins

    // Try to make another move
    const emptyCell = document.querySelector('[data-cell-index="5"]');
    emptyCell.click();
    assert(GameState.board[5] === '', 'Cell should remain empty after game ends');
});

// Test 15: Reset functionality
test('Reset button clears board and restarts game', () => {
    resetGame();
    // Make some moves
    document.querySelector('[data-cell-index="0"]').click();
    document.querySelector('[data-cell-index="1"]').click();

    // Reset
    resetGame();

    assert(GameState.board.every(cell => cell === ''), 'Board should be empty after reset');
    assert(GameState.currentPlayer === 'X', 'Should reset to player X');
    assert(GameState.gameActive === true, 'Game should be active after reset');

    const cells = document.querySelectorAll('.cell');
    cells.forEach(cell => {
