        { name: 'Middle column [1,4,7]', pattern: /\[1,\s*4,\s*7\]/ },
        { name: 'Right column [2,5,8]', pattern: /\[2,\s*5,\s*8\]/ },
        { name: 'Diagonal TL-BR [0,4,8]', pattern: /\[0,\s*4,\s*8\]/ },
        { name: 'Diagonal TR-BL [2,4,6]', pattern: /\[2,\s*4,\s*6\]/ }
    ];

    rowTests.forEach(test => {
        const passed = test.pattern.test(combosStr);
