    { name: 'handleWin', purpose: 'Handle win' },
    { name: 'handleTie', purpose: 'Handle tie' },
    { name: 'switchPlayer', purpose: 'Alternate players' },
    { name: 'resetGame', purpose: 'Reset game' }
];

functions.forEach(func => {
    const pattern = new RegExp(`function\\s+${func.name}\\s*\\(`);
    const passed = pattern.test(js);
    tests.push({
        test: `Function exists: ${func.name}`,
        passed: passed,
        evidence: passed ? 'Function defined in code' : 'Function not found'
    });
    console.log(`${passed ? 'âœ…' : 'âŒ'} ${func.name}(): ${passed ? 'EXISTS' : 'MISSING'}`);
});

console.log();

// Test 3: Check for proper game state validation
const validations = [
    { name: 'Check if cell is empty', pattern: /GameState\.board\[.*?\]\s*!==\s*['"]/ },
    { name: 'Check if game is active', pattern: /!GameState\.gameActive/ },
