        passed: passed,
        evidence: passed ? 'Validation found in code' : 'Validation missing'
    });
    console.log(`${passed ? 'âœ…' : 'âŒ'} ${val.name}: ${passed ? 'FOUND' : 'MISSING'}`);
});

console.log();

// Test 4: Check for tie detection
const tieCheck = /!GameState\.board\.includes\(['"]|board\.every/.test(js);
tests.push({
    test: 'Tie detection when board is full',
    passed: tieCheck,
    evidence: tieCheck ? 'Board fullness check found' : 'No board fullness check'
});
console.log(`${tieCheck ? 'âœ…' : 'âŒ'} Tie detection: ${tieCheck ? 'IMPLEMENTED' : 'MISSING'}`);

// Test 5: Check for score tracking
const scoreTracking = /scores\.X|scores\[GameState\.currentPlayer\]/.test(js);
tests.push({
    test: 'Score tracking functionality',
    passed: scoreTracking,
    evidence: scoreTracking ? 'Score updates found in code' : 'No score updates'
});
console.log(`${scoreTracking ? 'âœ…' : 'âŒ'} Score tracking: ${scoreTracking ? 'IMPLEMENTED' : 'MISSING'}`);

// Test 6: Check for reset functionality
const resetCheck = /GameState\.board\s*=\s*\[/.test(js);
tests.push({
    test: 'Board reset functionality',
    passed: resetCheck,
    evidence: resetCheck ? 'Board reset found in resetGame()' : 'No board reset'
});
console.log(`${resetCheck ? 'âœ…' : 'âŒ'} Board reset: ${resetCheck ? 'IMPLEMENTED' : 'MISSING'}`);

// Test 7: Check for event listeners
const eventListeners = /addEventListener/.test(js);
tests.push({
    test: 'Event listeners attached',
    passed: eventListeners,
    evidence: eventListeners ? 'Event listeners found' : 'No event listeners'
});
console.log(`${eventListeners ? 'âœ…' : 'âŒ'} Event listeners: ${eventListeners ? 'FOUND' : 'MISSING'}`);

// Test 8: Check cells are disabled after game ends
const disableOnEnd = /cells\.forEach.*disabled\s*=\s*true/.test(js);
tests.push({
    test: 'Cells disabled after game ends',
    passed: disableOnEnd,
    evidence: disableOnEnd ? 'Cell disabling found in handleWin/handleTie' : 'No cell disabling'
});
console.log(`${disableOnEnd ? 'âœ…' : 'âŒ'} Disable cells on end: ${disableOnEnd ? 'IMPLEMENTED' : 'MISSING'}`);

console.log();
console.log('='.repeat(50));

const passed = tests.filter(t => t.passed).length;
const failed = tests.filter(t => !t.passed).length;

console.log(`ðŸ“Š Analysis Complete: ${passed} passed, ${failed} failed`);
console.log('='.repeat(50));

// Output JSON
console.log('\nðŸ“‹ JSON Results:');
console.log(JSON.stringify({ passed, failed, tests }, null, 2));

process.exit(failed > 0 ? 1 : 0);

