// Test HTML structure
const fs = require('fs');

console.log('ðŸ§ª HTML Structure Tests\n');

const html = fs.readFileSync('index.html', 'utf8');
const css = fs.readFileSync('styles.css', 'utf8');

const tests = [];

// Test 1: Has 9 cells for 3x3 grid
const cellMatches = html.match(/data-cell-index="/g);
const cellCount = cellMatches ? cellMatches.length : 0;
const test1 = cellCount === 9;
tests.push({
    test: '3x3 grid has exactly 9 cells',
    passed: test1,
    evidence: `Found ${cellCount} cells with data-cell-index`
});
console.log(`${test1 ? 'âœ…' : 'âŒ'} 3x3 grid: ${cellCount} cells ${test1 ? '(correct)' : '(incorrect)'}`);

// Test 2: Cells are clickable (button elements)
const buttonCells = (html.match(/<button[^>]*class="cell"/g) || []).length;
const test2 = buttonCells === 9;
tests.push({
    test: 'Cells are clickable buttons',
    passed: test2,
    evidence: `Found ${buttonCells} button elements with cell class`
});
console.log(`${test2 ? 'âœ…' : 'âŒ'} Clickable cells: ${buttonCells} button elements ${test2 ? '(correct)' : '(incorrect)'}`);

// Test 3: Has status/turn display
const statusDisplay = /id="status"|id="current-player"/.test(html);
tests.push({
    test: 'Current player turn display exists',
    passed: statusDisplay,
    evidence: statusDisplay ? 'Found status/current-player element' : 'No status display found'
});
console.log(`${statusDisplay ? 'âœ…' : 'âŒ'} Turn display: ${statusDisplay ? 'EXISTS' : 'MISSING'}`);

// Test 4: Has reset button
const resetBtn = /id="reset-btn"|New Game/.test(html);
tests.push({
    test: 'Reset button exists',
    passed: resetBtn,
    evidence: resetBtn ? 'Found reset button' : 'No reset button found'
});
console.log(`${resetBtn ? 'âœ…' : 'âŒ'} Reset button: ${resetBtn ? 'EXISTS' : 'MISSING'}`);

// Test 5: Has scoreboard
const scoreBoard = /id="x-score"|id="o-score"|id="tie-score"/.test(html);
tests.push({
    test: 'Score tracking display exists',
    passed: scoreBoard,
    evidence: scoreBoard ? 'Found score elements' : 'No score elements found'
});
console.log(`${scoreBoard ? 'âœ…' : 'âŒ'} Scoreboard: ${scoreBoard ? 'EXISTS' : 'MISSING'}`);

// Test 6: Proper viewport meta tag (responsive design)
const viewport = /<meta name="viewport"/.test(html);
tests.push({
    test: 'Responsive viewport meta tag',
    passed: viewport,
    evidence: viewport ? 'Viewport meta tag found' : 'No viewport meta tag'
});
console.log(`${viewport ? 'âœ…' : 'âŒ'} Responsive design: ${viewport ? 'ENABLED' : 'MISSING'}`);

// Test 7: CSS has grid layout
const gridLayout = /display:\s*grid/.test(css);
tests.push({
    test: 'CSS uses grid layout for board',
    passed: gridLayout,
    evidence: gridLayout ? 'display: grid found in CSS' : 'No grid layout found'
});
console.log(`${gridLayout ? 'âœ…' : 'âŒ'} Grid layout: ${gridLayout ? 'IMPLEMENTED' : 'MISSING'}`);

// Test 8: CSS has hover effects
const hoverEffects = /\.cell:hover/.test(css);
tests.push({
    test: 'Hover effects on cells',
    passed: hoverEffects,
    evidence: hoverEffects ? '.cell:hover styles found' : 'No hover styles'
});
console.log(`${hoverEffects ? 'âœ…' : 'âŒ'} Hover effects: ${hoverEffects ? 'IMPLEMENTED' : 'MISSING'}`);

// Test 9: CSS has animations
const animations = /@keyframes|animation:/.test(css);
tests.push({
    test: 'CSS animations/visual effects',
    passed: animations,
    evidence: animations ? 'Animations found in CSS' : 'No animations'
});
console.log(`${animations ? 'âœ…' : 'âŒ'} Animations: ${animations ? 'IMPLEMENTED' : 'MISSING'}`);

// Test 10: CSS has responsive breakpoints
const responsive = /@media/.test(css);
tests.push({
    test: 'Responsive design for mobile',
    passed: responsive,
    evidence: responsive ? 'Media queries found' : 'No media queries'
});
console.log(`${responsive ? 'âœ…' : 'âŒ'} Mobile responsive: ${responsive ? 'IMPLEMENTED' : 'MISSING'}`);

// Test 11: Accessibility - ARIA labels
const ariaLabels = /aria-label/.test(html);
tests.push({
    test: 'ARIA labels for accessibility',
    passed: ariaLabels,
    evidence: ariaLabels ? 'ARIA labels found' : 'No ARIA labels'
});
console.log(`${ariaLabels ? 'âœ…' : 'âŒ'} ARIA accessibility: ${ariaLabels ? 'IMPLEMENTED' : 'MISSING'}`);

// Test 12: Links to CSS and JS files
const cssLink = /<link[^>]*href="styles\.css"/.test(html);
const jsLink = /<script[^>]*src="script\.js"/.test(html);
const linksWork = cssLink && jsLink;
tests.push({
    test: 'CSS and JS files properly linked',
    passed: linksWork,
    evidence: `CSS: ${cssLink}, JS: ${jsLink}`
});
console.log(`${linksWork ? 'âœ…' : 'âŒ'} File linking: ${linksWork ? 'CORRECT' : 'MISSING'}`);

console.log();
console.log('='.repeat(50));

const passed = tests.filter(t => t.passed).length;
const failed = tests.filter(t => !t.passed).length;

console.log(`ðŸ“Š HTML/CSS Tests: ${passed} passed, ${failed} failed`);
console.log('='.repeat(50));

console.log('\nðŸ“‹ JSON Results:');
console.log(JSON.stringify({ passed, failed, tests }, null, 2));

process.exit(failed > 0 ? 1 : 0);

